import React, { useState, useEffect } from 'react';
import { fetchDisasters } from '../services/apiService';

const HomePage = () => {
    const [disasters, setDisasters] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const getDisasters = async () => {
        try {
            const data = await fetchDisasters();
            setDisasters(data.DisasterDeclarationsSummaries || []);
            setLoading(false);
        } catch (error) {
            console.error('Error fetching disasters:', error);
            setLoading(false);
        }
        };
        getDisasters();
    }, []);

    return (
        <div className="p-6">
        <h1 className="text-3xl font-bold text-center mb-4">Disaster Early Warning System</h1>
        {loading ? (
            <p className="text-center text-gray-500">Loading...</p>
        ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {disasters.map((disaster) => (
                <div key={disaster.disasterNumber} className="bg-white p-4 shadow-md rounded-md">
                <h2 className="text-xl font-semibold">{disaster.title}</h2>
                <p><strong>Type:</strong> {disaster.incidentType}</p>
                <p><strong>State:</strong> {disaster.state}</p>
                <p><strong>Date:</strong> {new Date(disaster.declarationDate).toLocaleDateString()}</p>
                </div>
            ))}
            </div>
        )}
        </div>
    );
};

export default HomePage;
